targets.expr:
sum(
  vmware_vm_cpu_corecount{vcenter=~"$vcenter"} * on (vmmo) group_left 
  vmware_vm_info{vcenter=~"$vcenter", hostmo=~"$hostmo"}
)
/ 
sum(vmware_host_cpu_threadcount{vcenter=~"$vcenter", hostmo=~"$hostmo"})

sum(
    vmware_vm_mem_capacity{vcenter=~"$vcenter", hostmo=~"$hostmo"}
) / 
sum(
   vmware_host_cpu_corecount{ vcenter=~"$vcenter", hostmo=~"$hostmo"}
)

topk(
  $top,
  max_over_time(
    (
      vmware_vm_cpu_usagemhz_average{vcenter=~"$vcenter"}
        * on (vmmo) group_left
          vmware_vm_info{vcenter=~"$vcenter", hostmo=~"$hostmo"}
    )[$__range:]
  )
) * 1000 * 1000

topk(
  $top,
  max_over_time(
    (
      vmware_vm_mem_consumed_average{vcenter=~"$vcenter"}
        * on (vmmo) group_left
          vmware_vm_info{vcenter=~"$vcenter", hostmo=~"$hostmo"}
    )[$__range:]
  )
)

topk(
  $top,
  max_over_time(
    (
      sum(
        vmware_vm_net_bytesRx_average{
          vcenter=~"$vcenter",
          pfinstance!~"^vmnic.*|^vusb.*"
        }
      ) by (vm)
        * on (vm) group_left
          vmware_vm_info{vcenter=~"$vcenter", hostmo=~"$hostmo"}
    )[$__range:]
  )
) * 8192

topk(
  $top,
  max_over_time(
    (
      sum(
        vmware_vm_net_bytesTx_average{
          vcenter=~"$vcenter",
          pfinstance!~"^vmnic.*|^vusb.*"
        }
      ) by (vm)
        * on (vm) group_left
          vmware_vm_info{vcenter=~"$vcenter", hostmo=~"$hostmo"}
    )[$__range:]
  )
) * 8192

count(
    vmware_vm_info{vcenter=~"$vcenter", hostmo=~"$hostmo"} * on (hostmo) group_left
    vmware_host_info{vcenter=~"$vcenter", hostmo=~"$hostmo", host=~"$host"}
)

sum(
  vmware_host_cpu_usagemhz_average{vcenter=~"$vcenter", hostmo=~"$hostmo"} * on (hostmo) group_left 
  vmware_host_info{vcenter=~"$vcenter", hostmo=~"$hostmo", host=~"$host"}
) /
sum(
  (
    vmware_host_cpu_capacity{vcenter=~"$vcenter", hostmo=~"$hostmo"} * on (hostmo) group_left 
vmware_host_info{vcenter=~"$vcenter", hostmo=~"$hostmo", host=~"$host"}
  ) * 
  (
    vmware_host_cpu_corecount{vcenter=~"$vcenter", hostmo=~"$hostmo"} * on (hostmo) group_left 
vmware_host_info{vcenter=~"$vcenter", hostmo=~"$hostmo", host=~"$host"}
  )
) 
* 100

sum(
    vmware_host_mem_consumed_average{vcenter=~"$vcenter", hostmo=~"$hostmo"} * 1024 * on (hostmo) group_left 
vmware_host_info{vcenter=~"$vcenter", hostmo=~"$hostmo", host=~"$host"}
) / 
sum( 
    vmware_host_mem_capacity{vcenter=~"$vcenter", hostmo=~"$hostmo"} * on (hostmo) group_left 
vmware_host_info{vcenter=~"$vcenter", hostmo=~"$hostmo", host=~"$host"}
)
* 100

sum(
  vmware_vm_cpu_corecount{vcenter=~"$vcenter"} * on (hostmo) group_left 
  vmware_host_info{vcenter=~"$vcenter", hostmo=~"$hostmo", host=~"$host"}
)
/ 
vmware_host_cpu_threadcount{vcenter=~"$vcenter", hostmo=~"$hostmo"} * on (hostmo) group_left
vmware_host_info{vcenter=~"$vcenter", hostmo=~"$hostmo", host=~"$host"}

sum(
    vmware_vm_mem_capacity{vcenter=~"$vcenter", hostmo=~"$hostmo"}
) / 
sum(
   vmware_host_cpu_corecount{ vcenter=~"$vcenter", hostmo=~"$hostmo"}
)
