targets.expr:
topk(
  $top,
  max_over_time(
    (
      vmware_vm_cpu_usagemhz_average{vcenter=~"$vcenter"}
        * on (vmmo) group_left
          vmware_vm_info{vcenter=~"$vcenter", hostmo="$hostmo"}
    )[$__range:]
  )
) * 1000 * 1000

topk(
  $top,
  max_over_time(
    (
      vmware_vm_mem_consumed_average{vcenter=~"$vcenter"}
        * on (vmmo) group_left
          vmware_vm_info{vcenter=~"$vcenter", hostmo=~"$hostmo"}
    )[$__range:]
  )
)

sum(
  vmware_vm_cpu_corecount{vcenter=~"$vcenter"} * on (vmmo) group_left 
  vmware_vm_info{vcenter=~"$vcenter", hostmo="$hostmo"}
)
/ 
(vmware_host_cpu_threadcount{vcenter=~"$vcenter", hostmo="$hostmo"})

 sum(
  vmware_vm_mem_capacity{vcenter=~"$vcenter"} * on (vmmo) group_left 
  vmware_vm_info{vcenter=~"$vcenter", hostmo="$hostmo"} * 1024 * 1024
)
/
vmware_host_mem_capacity{vcenter=~"$vcenter", hostmo="$hostmo"}

sum(
    vmware_vm_mem_capacity{vcenter=~"$vcenter", hostmo=~"$hostmo"}
) / 
sum(
   vmware_host_cpu_corecount{ vcenter=~"$vcenter", hostmo=~"$hostmo"}
)
